import {
	StyleSheet,
	View,
	KeyboardAvoidingView,
	Text,
	ImageBackground,
	TextInput,
	Platform,
	Dimensions, KeyboardAvoidingViewComponent, Button,
	ScrollView
} from "react-native";
import {NativeModules} from 'react-native';
import Banner from './Banner';
import {useState} from "react";

export default function Login(props) {
	let [email, setEmail] = useState("");

	return (
		<KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding" : "height"} >
			<ImageBackground source={require("../../assets/login_bg_2.png")} style={styles.background_image}>
			</ImageBackground>
			<ScrollView style={styles.container}>
			<View style={styles.content_container}>
				<Banner/>
				<View style={styles.inputs_container}>
					<TextInput style={styles.input} placeholder="Email" placeholderTextColor="#A3A9AA" onEndEditing={e => {
						setEmail(e.nativeEvent.text);
						console.log(e.nativeEvent.text);
					}}/>
					<TextInput secureTextEntry={true} style={styles.input} placeholder={email} placeholderTextColor="#A3A9AA"/>
					<View style={{width: "100%"}}>
						<Button title={"Login"}/>
					</View>
				</View>
			</View>
			</ScrollView>
		</KeyboardAvoidingView>
	);
}

const dimension = Dimensions.get("screen");
const {StatusBarManager} = NativeModules;
const statusBarHeight = Platform.OS === "ios" ? 20 : StatusBarManager.HEIGHT;

const styles = StyleSheet.create({
	container: {
		paddingTop: statusBarHeight
	},

	content_container: {
		paddingTop: "20%",
		justifyContent: "center",
		alignItems: "center"
	},

	inputs_container: {
		marginTop: 50,
		width: "80%",
	},

	input: {
		backgroundColor: "#222222",
		borderColor: "#A3A9AA",
		borderStyle: "solid",
		borderRadius: 5,
		borderWidth: 2,
		paddingVertical: 7,
		paddingHorizontal: 10,
		color: "#A3A9AA",
		fontSize: 16,
		width: "100%",
		marginBottom: 15,
		alignSelf: "center"
	},

	background_image: {
		position: "absolute",
		height: dimension.height,
		width: dimension.width
	},

	button: {
		borderRadius: 5
	}
});
