import {useState} from "react";

export default function useEmailField () {
	const regex = "/^\\w+([\\.-]?\\w+)*@\\w+([\\.-]?\\w+)*(\\.\\w{2,3})+$/";

	const [email, setEmail] = useState("");

	const isValidStyling = () => {
		return regex.match(email);
	}

	return {email, setEmail, isValidStyling};
}
