import {
	StyleSheet,
	View,
	KeyboardAvoidingView,
	Text,
	ImageBackground,
	TextInput,
	Platform,
	Dimensions, KeyboardAvoidingViewComponent, Button,
} from "react-native";
import {NativeModules} from 'react-native';
import Banner from './Banner';

export default function Login(props) {
	return (
		<KeyboardAvoidingView style={styles.container} behavior={Platform.OS === "ios" ? "padding" : "height"}>
			<ImageBackground source={require("../../assets/login_bg.png")} style={styles.background_image}>
			</ImageBackground>
			<Banner/>
			<View style={styles.inputs_container}>
				<TextInput style={styles.input} placeholder="Email" placeholderTextColor="#A3A9AA"/>
				<TextInput style={styles.input} placeholder="Password" placeholderTextColor="#A3A9AA"/>
			</View>
		</KeyboardAvoidingView>
	);
}

const dimension = Dimensions.get("screen");
const {StatusBarManager} = NativeModules;
const statusBarHeight = Platform.OS === "ios" ? 20 : StatusBarManager.HEIGHT;

const styles = StyleSheet.create({
	container: {
		display: "flex",
		paddingTop: statusBarHeight,
	},

	inputs_container: {
		display: "flex",
		alignItems: "center",
		justifyContent: "center",
	},

	input: {
		backgroundColor: "#222222",
		borderColor: "#1C5D99",
		borderStyle: "solid",
		borderRadius: 5,
		borderWidth: 2,
		paddingVertical: 7,
		paddingHorizontal: 10,
		color: "#A3A9AA",
		fontSize: 16,
		width: "80%",
		marginBottom: 15
	},

	background_image: {
		position: "absolute",
		height: dimension.height,
		width: dimension.width
	}
});
